import React, {Component} from 'react';
import {Link} from "react-router-dom";

class Product extends Component {

    render() {

        const {id, img, price, inCart, title} = this.props.product;

        return (
            <div className="card col-9 mx-auto col-md-6 col-lg-3 mb-3 mr-3">
                <div className="img-container py-5">
                    <img src={img} alt="phone" className="card-img-top" />
                </div>
                <Link to="/details">
                    <button
                        className="cart-btn"
                        onClick={() => console.log("You clicked the cart button")}
                        disabled={!!inCart}
                    >
                        {inCart?
                            (<p>In Cart</p>):
                            <i className="fas fa-cart-plus" />
                        }
                    </button>
                </Link>
            </div>
        );
    }
}

export default Product;
